definition.script:
pipeline {
    agent any
    environment {
        PROJECT_NAME = "android-app"
        PROJECT_DIR  = "${env.PROJECTS_DIR}/arcana-android"
    }
    options {
        timeout(time: 60, unit: "MINUTES")
        disableConcurrentBuilds()
        buildDiscarder(logRotator(numToKeepStr: "10"))
    }
    stages {
        stage("Checkout") {
            steps { echo "Using cloned repo: arcana-android" }
        }
        stage("Docker Compose Build") {
            steps {
                dir("${PROJECT_DIR}") {
                    sh "docker compose -f docker-compose.ci.yml build"
                }
            }
        }
        stage("Gradle Build") {
            steps {
                dir("${PROJECT_DIR}") {
                    sh "docker compose -f docker-compose.ci.yml run --rm android-build"
                }
            }
        }
        stage("SonarQube Analysis") {
            steps {
                dir("${PROJECT_DIR}") {
                    catchError(buildResult: 'SUCCESS', stageResult: 'UNSTABLE') {
                        withSonarQubeEnv('SonarQube') {
                            sh '''sonar-scanner \
                              -Dsonar.projectKey=android-app \
                              -Dsonar.projectName="Android App" \
                              -Dsonar.sources=. \
                              -Dsonar.exclusions=**/build/**,**/.gradle/**,**/docs/**,**/*.java,**/*.kt \
                              -Dsonar.java.binaries=. \
                              -Dsonar.scm.disabled=true \
                              -Dsonar.coverage.jacoco.xmlReportPaths='''
                        }
                    }
                }
            }
        }
        stage("Verify APK") {
            steps {
                dir("${PROJECT_DIR}") {
                    sh "ls -la app/build/outputs/apk/debug/ || echo 'APK directory not found'"
                }
            }
        }
    }
    post {
        success { echo "Android build succeeded: ${PROJECT_NAME}" }
        failure { echo "Android build FAILED: ${PROJECT_NAME}" }
        always {
            dir("${PROJECT_DIR}") {
                sh "docker compose -f docker-compose.ci.yml down --remove-orphans || true"
            }
        }
    }
}
