definition.script:
pipeline {
    agent any
    environment {
        APP_NAME    = "esp32-app"
        VERSION     = "1.0.0"
        PROJECT_DIR = "${env.PROJECTS_DIR}/arcana-embedded-esp32"
    }
    stages {
        stage("Checkout") {
            steps { echo "Using cloned repo: arcana-embedded-esp32" }
        }
        stage("Pull Build Image") {
            steps { sh "docker pull espressif/idf:v5.5.2" }
        }
        stage("Build Firmware") {
            steps {
                dir("${PROJECT_DIR}") {
                    sh "docker compose -f docker-compose.ci.yml run --rm esp32-build"
                }
            }
        }
        stage("Extract Artifacts") {
            steps {
                sh "rm -rf /tmp/esp32-app-firmware && mkdir -p /tmp/esp32-app-firmware"
                sh "cp ${PROJECT_DIR}/build/mqtt5.bin /tmp/esp32-app-firmware/ || echo no-bin"
                sh "cp ${PROJECT_DIR}/build/bootloader/bootloader.bin /tmp/esp32-app-firmware/ || echo no-bootloader"
                sh "cp ${PROJECT_DIR}/build/partition_table/partition-table.bin /tmp/esp32-app-firmware/ || echo no-partition"
                sh "ls -la /tmp/esp32-app-firmware/"
            }
        }
    }
    post {
        success { echo "Pipeline SUCCESS - esp32 firmware built" }
        failure { echo "Pipeline FAILED" }
        always  { echo "Build done" }
    }
}
