definition.script:
pipeline {
    agent any
    environment {
        PROJECT_NAME = "harmonyos-app"
    }
    options {
        timeout(time: 90, unit: "MINUTES")
        disableConcurrentBuilds()
    }
    stages {
        stage("Build HAP") {
            steps {
                dir("/home/jrjohn/devops-test/arcana-harmonyos") {
                    sh "docker compose -f docker-compose.ci.yml build"
                    sh "docker compose -f docker-compose.ci.yml run --rm harmonyos-build"
                }
            }
        }
        stage("Verify HAP") {
            steps {
                dir("/home/jrjohn/devops-test/arcana-harmonyos") {
                    sh "ls -la entry/build/default/outputs/default/*.hap 2>/dev/null || echo HAP not found"
                }
            }
        }
    }
    post {
        success { echo "HarmonyOS build succeeded: ${PROJECT_NAME}" }
        failure { echo "HarmonyOS build FAILED: ${PROJECT_NAME}" }
        always {
            dir("/home/jrjohn/devops-test/arcana-harmonyos") {
                sh "docker compose -f docker-compose.ci.yml down --remove-orphans 2>/dev/null || true"
            }
        }
    }
}
