definition.script:
pipeline {
    agent { label "macos" }
    environment {
        PROJECT_NAME = "arcana-ios"
        PATH = "/opt/homebrew/bin:/usr/local/bin:/usr/bin:/bin:/usr/sbin:/sbin"
    }
    options {
        timeout(time: 15, unit: "MINUTES")
        disableConcurrentBuilds()
        buildDiscarder(logRotator(numToKeepStr: "10"))
    }
    stages {
        stage("Checkout") {
            steps {
                dir("/Users/jenkins/devops-test/arcana-ios") {
                    script { env.VERSION = "1.0.0" }
                    echo "Building ${PROJECT_NAME} v${VERSION}"
                }
            }
        }
        stage("Build") {
            steps {
                dir("/Users/jenkins/devops-test/arcana-ios") {
                    sh "xcodebuild -project arcana-ios.xcodeproj -scheme arcana-ios -configuration Release -destination 'generic/platform=iOS' CODE_SIGNING_ALLOWED=NO build 2>&1 | tail -20"
                }
            }
        }
        stage("Test") {
            steps {
                dir("/Users/jenkins/devops-test/arcana-ios") {
                    sh "xcodebuild -project arcana-ios.xcodeproj -scheme arcana-ios -destination 'platform=iOS Simulator,name=iPhone 16' test 2>&1 | tail -30"
                }
            }
        }
    }
    post {
        success { echo "iOS build succeeded: ${PROJECT_NAME} v${VERSION}" }
        failure { echo "iOS build FAILED: ${PROJECT_NAME}" }
    }
}
