definition.script:
pipeline {
    agent any
    triggers {
        pollSCM('H/5 * * * *')
    }
    environment {
        APP_NAME  = "rust-app"
        REGISTRY  = "localhost:5000"
        IMAGE_TAG = "${REGISTRY}/arcana/${APP_NAME}"
        VERSION   = "1.0.0"
    }
    stages {
        stage("Checkout") {
            steps {
                dir("${env.PROJECTS_DIR}/arcana-cloud-rust") {
                    git url: 'https://github.com/jrjohn/arcana-cloud-rust.git',
                        branch: 'main', credentialsId: 'github-credentials'
                }
            }
        }
                stage("Cleanup Old Images") {
            steps {
                dir("${env.PROJECTS_DIR}/arcana-cloud-rust") {
                    sh '''
                        # Remove dangling/unused images to free disk space
                        docker image prune -f || true
                        # Keep only last 3 build-tagged images for this app
                        docker images --format '{{.Repository}}:{{.Tag}}' \
                            | grep "${APP_NAME}.*build-" \
                            | sort -t- -k2 -rn \
                            | tail -n +4 \
                            | xargs -r docker rmi 2>/dev/null || true
                        # Stop leftover test containers
                        docker compose -f docker-compose.test.yml down \
                            --remove-orphans 2>/dev/null || true
                    '''
                }
            }
        }
        stage("Docker Compose Build") {
            steps {
                dir("${env.PROJECTS_DIR}/arcana-cloud-rust") {
                    sh "VERSION=${VERSION} docker compose -f docker-compose.ci.yml build"
                    sh "docker tag localhost:5000/arcana/${APP_NAME}:${VERSION} ${IMAGE_TAG}:build-${BUILD_NUMBER}"
                }
            }
        }
        stage("Unit Tests") {
            steps {
                dir("${env.PROJECTS_DIR}/arcana-cloud-rust") {
                    catchError(buildResult: 'SUCCESS', stageResult: 'UNSTABLE') {
                        sh "docker compose -f docker-compose.test.yml run --rm --build test"
                    }
                }
            }
        }
        stage("SonarQube Analysis") {
            steps {
                dir("${env.PROJECTS_DIR}/arcana-cloud-rust") {
                    catchError(buildResult: 'SUCCESS', stageResult: 'UNSTABLE') {
                        withSonarQubeEnv('SonarQube') {
                            sh '''sonar-scanner \
                              -Dsonar.projectKey=rust-app \
                              -Dsonar.projectName="Rust App" \
                              -Dsonar.sources=crates \
                              -Dsonar.exclusions=target/**,**/target/** \
                              -Dsonar.rust.clippy.enabled=false \
                              -Dsonar.scm.disabled=true'''
                        }
                    }
                }
            }
        }
        stage("Image Info") {
            steps {
                sh "docker images --format 'table {{.Repository}}:{{.Tag}}\t{{.Size}}' ${IMAGE_TAG}:${VERSION}"
            }
        }
        stage("Push to Registry") {
            steps {
                sh "docker push ${IMAGE_TAG}:${VERSION}"
                sh "docker push ${IMAGE_TAG}:build-${BUILD_NUMBER}"
            }
        }
    }
    post {
        success { echo "Pipeline SUCCESS - ${APP_NAME}:${VERSION}" }
        failure { echo "Pipeline FAILED" }
        always  { echo "Build number ${BUILD_NUMBER} done" }
    }
}
