definition.script:
pipeline {
    agent any
    triggers {
        pollSCM('H/5 * * * *')
    }
    environment {
        APP_NAME  = "vue-app"
        REGISTRY  = "localhost:5000"
        IMAGE_TAG = "${REGISTRY}/arcana/${APP_NAME}"
        VERSION   = "1.0.0"
    }
    stages {
        stage("Checkout") {
            steps {
                dir("${env.PROJECTS_DIR}/arcana-vue") {
                    git url: 'https://github.com/jrjohn/arcana-vue.git',
                        branch: 'main', credentialsId: 'github-credentials'
                }
            }
        }
        stage("Docker Compose Build") {
            steps {
                dir("${env.PROJECTS_DIR}/arcana-vue") {
                    sh "VERSION=${VERSION} docker compose -f docker-compose.ci.yml build"
                    sh "docker tag localhost:5000/arcana/${APP_NAME}:${VERSION} ${IMAGE_TAG}:build-${BUILD_NUMBER}"
                }
            }
        }
        stage("Unit Tests") {
            steps {
                dir("${env.PROJECTS_DIR}/arcana-vue") {
                    catchError(buildResult: 'SUCCESS', stageResult: 'UNSTABLE') {
                        sh "docker compose -f docker-compose.test.yml run --rm --build test"
                    }
                }
            }
        }
        stage("SonarQube Analysis") {
            steps {
                dir("${env.PROJECTS_DIR}/arcana-vue") {
                    catchError(buildResult: 'SUCCESS', stageResult: 'UNSTABLE') {
                        withSonarQubeEnv('SonarQube') {
                            sh '''sonar-scanner \
                              -Dsonar.projectKey=vue-app \
                              -Dsonar.projectName="Vue App" \
                              -Dsonar.sources=src \
                              -Dsonar.exclusions=node_modules/**,dist/** \
                              -Dsonar.scm.disabled=true \
                              -Dsonar.javascript.lcov.reportPaths=coverage/lcov.info'''
                        }
                    }
                }
            }
        }
        stage("Image Info") {
            steps { sh "docker images --format 'table {{.Repository}}:{{.Tag}}\t{{.Size}}' ${IMAGE_TAG}:${VERSION}" }
        }
        stage("Push to Registry") {
            steps {
                sh "docker push ${IMAGE_TAG}:${VERSION}"
                sh "docker push ${IMAGE_TAG}:build-${BUILD_NUMBER}"
            }
        }
    }
    post {
        success { echo "Pipeline SUCCESS - ${APP_NAME}:${VERSION}" }
        failure { echo "Pipeline FAILED" }
        always  { echo "Build number ${BUILD_NUMBER} done" }
    }
}
